package solutions.No_0438_final_all_anagrams_in_a_string;

import java.util.*;

/**
 * https://leetcode.com/problems/find-all-anagrams-in-a-string/
 * @author Nacht
 * Created on 2019/5/27
 * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
 * Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.
 * Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 20,100.
 * The order of output does not matter.
 * Example 1:
 * Input:
 * s: "cbaebabacd" p: "abc"
 * Output:
 * [0, 6]
 * Explanation:
 * The substring with start index = 0 is "cba", which is an anagram of "abc".
 * The substring with start index = 6 is "bac", which is an anagram of "abc".
 * Example 2:
 * Input:
 * s: "abab" p: "ab"
 * Output:
 * [0, 1, 2]
 * Explanation:
 * The substring with start index = 0 is "ab", which is an anagram of "ab".
 * The substring with start index = 1 is "ba", which is an anagram of "ab".
 * The substring with start index = 2 is "ab", which is an anagram of "ab".
 * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * * *
 * 解题思路:
 * 从0索引开始遍历s,每次截取长度为p长度的子串,判断是否有相同的字母即可
 * 用hashmap+滑动窗口解决
 * 这tm是简单的题,服了
 */
public class Solution {
    public List<Integer> findAnagrams(String s, String p) {
        List<Integer> resultList = new ArrayList<>();
        if("".equals(s)||s.length()<p.length()){
            return resultList;
        }
        if(s.equals(p)){
            resultList.add(0);
            return resultList;
        }
        HashMap<Character, Integer> pCharMap = new HashMap<Character,Integer>();
        for(char c : p.toCharArray()){
            pCharMap.put(c,pCharMap.containsKey(c)?pCharMap.get(c)+1:1);
        }
        HashMap<Character, Integer> sCharMap = new HashMap<>();
        for(int i = 0 ; i <  p.length()-1 ; i++){
            sCharMap.put(s.charAt(i),sCharMap.containsKey(s.charAt(i))?sCharMap.get(s.charAt(i))+1:1);
        }
        int i = 0 ;
        while(i <= s.length() - p.length()){
            char newChar = s.charAt(i+p.length()-1);
            sCharMap.put(newChar,sCharMap.containsKey(newChar)?sCharMap.get(newChar)+1:1);
            boolean isSameMap = true;
            for(Character c : sCharMap.keySet()){
                if(pCharMap.get(c)==null||(int) sCharMap.get(c)!=pCharMap.get(c)){
                    isSameMap = false;
                }
            }
            if(isSameMap){
                resultList.add(i);
            }
            if(sCharMap.get(s.charAt(i)) == 1) {
                sCharMap.remove(s.charAt(i));
            }else {
                sCharMap.put(s.charAt(i), sCharMap.get(s.charAt(i)) - 1);
            }
            i++;
        }
        return resultList;
    }

    public static void main(String[] args) {
        System.out.println(new Solution().findAnagrams("",
                ""));
    }
}
